import re
import os
import sys

class abyle_execute:
	def __init__(self):
		self.command = ""
		self.dryrun = False
	
	def run(self, command, dryrun):

		self.command = command	
		self.dryrun = dryrun

		stdOut = ""
		stdErr = ""

		if not self.dryrun:
			cmdIn, cmdOut, cmdErr = os.popen3(command)
			stdOut=cmdOut.readlines()
			stdErr=cmdErr.readlines()
		else:
			stdOut = ""
			stdErr = ""

		return stdOut, stdErr
